mapper.select:
SELECT sum(t_order.PAY_AMOUNT * t_agent.SHARGING_RATIO) from t_order, t_agent, t_shop, t_charging_box
t_agent.ID_NUMBER = #{queryDataMap.agentID}
AND t_order.PAY_STATUS = '1' AND t_shop.AG_ID = t_agent.ID AND t_charging_box.S_ID = t_shop.ID AND t_order.BOX_CHARGING_ID = t_charging_box.ID

SELECT sum(t_order.PAY_AMOUNT * (1 - t_agent.SHARGING_RATIO)) from t_order, t_agent, t_shop, t_charging_box
t_agent.ID = #{id}
AND t_order.PAY_STATUS = '1' AND t_agent.LEVEL != '1' AND t_agent.PARENT_AGENT_ID != 'NULL' AND t_shop.AG_ID = t_agent.ID AND t_charging_box.S_ID = t_shop.ID AND t_order.BOX_CHARGING_ID = t_charging_box.ID
